import React,{useEffect} from 'react'
import './IncludedAccordition.css'

function IncludedAccordition({activitie}) {

function openTabs(el) {
    
    var tabLinks = document.querySelectorAll(".tablinks");
    var tabContent = document.querySelectorAll(".tabcontent");
    
    console.log('kliknuo si')
   var btnTarget = el.currentTarget;
   var country = btnTarget.dataset.country;

   tabContent.forEach(function(el) {
      el.classList.remove("active");
   });

   tabLinks.forEach(function(el) {
      el.classList.remove("active");
   });

   document.querySelector("#" + country).classList.add("active");
   
   btnTarget.classList.add("active");
}

useEffect(() => {
    console.log('use effect odradjen!');
    
    var tabLinks = document.querySelectorAll(".tablinks");
    var tabContent = document.querySelectorAll(".tabcontent");
    
    console.log(tabLinks);
    tabLinks.forEach(function(el) {
        el.addEventListener("click", openTabs);
        
     });
    return () => {
         tabLinks.forEach(function(el){
             el.removeEventListener('click',openTabs);
         })
    }
},[] )

    return (

        <>
<section id="wrapper" className="included-navigation">
   <div className="content">
      <div className="tabs">
         <button className="tablinks active" data-country="Included"><p data-title="London">Included</p></button>
         <button className="tablinks" data-country="Not Included"><p data-title="Paris">Not Included</p></button>
         <button className="tablinks" data-country="What to bring"><p data-title="Barcelona">What to bring</p></button>
         <button className="tablinks" data-country="Additional Info"><p data-title="Madrid">Additional Info</p></button>
         
      </div>

      <div className="wrapper_tabcontent">
         <div id="London" className="tabcontent active">
            <h3>Included</h3>
            <ul>
                {activitie.included.map((include)=>{
                  return (<li>{include}</li>)
                })}
            </ul>
         </div>

         <div id="Paris" className="tabcontent">
            <h3>Not Included</h3>
            <p>Paris is in the Paris department of the Paris-Isle-of-France region The French historic, political and economic capital, with a population of only 2.5 million is located in the northern part of France. One of the most beautiful cities in the world. Home to historical monuments such as Notre Dame, the Eiffel tower (320m), Bastille, Louvre and many more. </p>
         </div>

         <div id="Barcelona" className="tabcontent">
            <h3>What to bring</h3>
            <p>Barcelona has been an urban laboratory since the high Medieval Ages. A place of diversity, a backdrop for a multiplicity of social and cultural processes on multiple scales that reflect different ways of constructing the future, a city with a long experience of urban life and social innovations. </p>
         </div>

         <div id="Madrid" className="tabcontent">
            <h3>Additional Info</h3>
            <p>Madrid is in the middle of Spain, in the Community of Madrid. The Community is a large area that includes the city as well as small towns and villages outside the city. 7 million people live in the Community. More than 3 million live in the city itself. 
            </p>
         </div>
      </div>
   </div>
</section>   
        </>
    )
}

export default IncludedAccordition
