import React from 'react'

function WeatherWidget() {
    const format_coordinates=(coordinates)=>{
        // Given a set of coordinates in the form {'lat': LAT, 'lng': LNG}
        // format them to weatherwidget.io expected url coordinate format.
        // The format is:
        // - coordinates rounded to 2nd decimal
        // - dots replaced by d
        // - minus signs replaced by n
        // - LAT and LNG concatenated
        
        coordinates['lat'] = round(coordinates['lat'], 2)
        coordinates['lng'] = round(coordinates['lng'], 2)
        lat = coordinates['lat']

        if (lat.split()[lat.length-1]=='.'){
            lat += "0"
        }
        lat = lat.replace(".", "d").replace("-", "n")

        lng = coordinates['lng']
        if (lng.split()[lng.length-1]=='.'){
            lng += "0"
        }
        lng = lng.replace(".", "d").replace("-", "n")
        return lat + lng
        }
    return (
            <div>
                <a className="weatherwidget-io" href="https:forecast7.com/en/n24d78n65d42/salta/" data-label_1="SALTA" data-label_2="CAPITAL" data-theme="original" >SALTA CAPITAL</a>

