import React,{useState,useEffect} from 'react'
import Carousel from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';
import { activities } from '../pages/HomePage/Data';

function DetailProductPage() {

    const [activitie,setActivitie]=useState([])


    const getBlob=()=>{ 
        /*ovdje bi stavio url tog koji ti treba*/
        fetch("https://jsonblob.com/api/jsonBlob/d98ac18e-6a1c-11eb-b59d-a157b950443c") 
              .then(response=>{
             return response.json();
              })
        .then(response=>{
            setActivitie(response[0]);

        })
              .catch(function(error){
                  console.log(error);
              });
      }

      useEffect(() => {
        getBlob();
    }, [])
    
    return (
        <div className='detail-product-page-container'>
            <div className='activitie-images'>
                <Carousel infinite arrows addArrowClickHandler 
                arrowLeft={<i id="left-icon" class="fas fa-chevron-left left-icon"></i>}
                arrowRight={<i id="right-icon" class="fas fa-chevron-right right-icon"></i>}        
                >
                    <img src={activitie.images[0]}></img>
                    <img src={activitie.images[1]}></img>
                    <img src={activitie.images[2]}></img>
                
                </Carousel>
            </div>
            <div className='activitie-data'>

            </div>
        </div>
    )
}

export default DetailProductPage
