          {_compact(slides).map((carouselSlide, index) => (
            <CarouselSlide
              clickable
              key={index}
              currentSlideIndex={activeSlideIndex || props.value}
              index={index}
              width={props.itemWidth || itemWidth}
              offset={index !== slides.length ? props.offset : 0}
